query GetFollowingActivities($first: Int, $after: String) {
  viewer {
    __typename
    followingActivities(first: $first, after: $after, orderBy: {field: CREATED_AT, direction: DESC}) {
      __typename
      edges {
        __typename
        cursor
        node {
          __typename
          ... on Record {
            ...RecordFragment
          }
          ... on Review {
            ...ReviewFragment
          }
          ... on Status {
            ...StatusFragment
          }
          ... on MultipleRecord {
            id
          }
        }
      }
      pageInfo {
        __typename
        endCursor
        hasNextPage
      }
    }
  }
}

d1a522475f872cdc8485a59600f54d67cac7f6bc.source:
query GetViewerWorks($first: Int, $after: String, $state: StatusState!) {
  viewer {
    __typename
    works(first: $first, after: $after, state: $state) {
      __typename
      edges {
        __typename
        node {
          __typename
          ...WorkFragment
        }
      }
    }
  }
}

999459cc336c9f020f1ff9e3492ae12d5bc379d1.source:
query SearchWorksByID($annictId: Int!, $after: String) {
  searchWorks(annictIds: [$annictId], after: $after) {
    __typename
    nodes {
      __typename
      ...WorkFragment
      episodes(first: 5, orderBy: {direction: ASC, field: SORT_NUMBER}) {
        __typename
        nodes {
          __typename
          id
          annictId
          title
          numberText
          viewerRecordsCount
        }
      }
    }
  }
}

f93d24477dc8c443991f3f85b8f44aca4a67ef9a.source:
query SearchWorksBySeason($first: Int, $after: String, $seasons: [String!]) {
  searchWorks(first: $first, after: $after, seasons: $seasons, orderBy: {direction: DESC, field: WATCHERS_COUNT}) {
    __typename
    edges {
      __typename
      node {
        __typename
        ...WorkFragment
      }
    }
  }
}
