query GetFollowingActivities($first: Int, $after: String) {
  viewer {
    __typename
    followingActivities(first: $first, after: $after, orderBy: {field: CREATED_AT, direction: DESC}) {
      __typename
      edges {
        __typename
        cursor
        node {
          __typename
          ... on Record {
            ...RecordFragment
          }
          ... on Review {
            id
          }
          ... on Status {
            id
          }
          ... on MultipleRecord {
            id
          }
        }
      }
      pageInfo {
        __typename
        endCursor
        hasNextPage
      }
    }
  }
}

