6c004fdf49fe0eb0fff3f8488ac0e096fad0793a.source:
query GetFollowingActivities($first: Int, $after: String) {
  viewer {
    __typename
    followingActivities(first: $first, after: $after, orderBy: {field: CREATED_AT, direction: DESC}) {
      __typename
      edges {
        __typename
        cursor
        node {
          __typename
          ... on Record {
            ...RecordFragment
          }
          ... on Review {
            ...ReviewFragment
          }
          ... on Status {
            ...StatusFragment
          }
          ... on MultipleRecord {
            id
          }
        }
      }
      pageInfo {
        __typename
        endCursor
        hasNextPage
      }
    }
  }
}

da28f4048d2537a3ee229489ef3b24356ca568d5.source:
query GetViewerWorks($first: Int, $after: String, $state: StatusState!) {
  viewer {
    __typename
    works(first: $first, after: $after, state: $state, orderBy: {direction: DESC, field: WATCHERS_COUNT}) {
      __typename
      edges {
        __typename
        node {
          __typename
          ...WorkFragment
        }
      }
    }
  }
}

bc7fdd907a96b06744895095e150fc76b0f84cf9.source:
query SearchWorkEpisodes($workAnnictId: Int!, $first: Int, $after: String) {
  searchWorks(annictIds: [$workAnnictId]) {
    __typename
    nodes {
      __typename
      ...WorkFragment
      episodes(first: $first, after: $after, orderBy: {direction: ASC, field: SORT_NUMBER}) {
        __typename
        edges {
          __typename
          node {
            __typename
            ...EpisodeFragment
          }
        }
        pageInfo {
          __typename
          endCursor
          hasNextPage
        }
      }
    }
  }
}

5e9902975ee56d522b235f58534e946edcdcda99.source:
query SearchWorksByID($annictId: Int!, $after: String) {
  searchWorks(annictIds: [$annictId], after: $after) {
    __typename
    nodes {
      __typename
      ...WorkFragment
      episodes(first: 5, orderBy: {direction: ASC, field: SORT_NUMBER}) {
        __typename
        nodes {
          __typename
          id
          annictId
          title
          numberText
          viewerRecordsCount
        }
      }
    }
  }
}

